create table food (foodid int primary key auto_increment, displayname_en varchar(255) not null default 'No name', displayname_fr varchar(255) not null default 'No name', toppingtype int, price float not null default 10, restaurantid int, isavailable int not null default 0, description varchar(255) not null default '', foodgroup int, imageurl varchar(255), foreign key (restaurantid) references restaurant(restaurantid));
